# Change the 'LastWriteTime' attribute of file or folder#if( $($args.count) -lt 2 )		# no parameter given, print usage:{	Write-Host "usage: $($MyInvocation.MYCommand) <file/folder> <'01/01/1900 0:00 AM'>"	Write-Host "   or: $($MyInvocation.MYCommand) <file/folder> <01/01/1900>"	Write-Host "   or: $($MyInvocation.MYCommand) <file/folder> <0:00>"	exit -1}if( $(Test-Path $args[0]) ){	$item = Get-Item $args[0]}else{	Write-Host "File or Folder $($args[0]) not present"	exit -1}if( $(Get-Date $args[1]) ){	$item.CreationTime = Get-Date $args[1]	$item.LastWriteTime = Get-Date $args[1]	$item.LastAccessTime = Get-Date $args[1]}else{	Write-Host "$($args[1]) is not a valid date and/or time"	exit -1}$item